package coding.leetcode

import org.assertj.core.api.Assertions
import org.junit.jupiter.api.Test

class Leet015ThreeSum {
    private fun threeSum(nums: IntArray): List<List<Int>> {
        if (nums.size < 3) return emptyList()
        // -1, 0, 1, 2, -1, -4
        // -4, -1, -1, 0, 1, 2
        val results = mutableSetOf<List<Int>>()
        nums.sorted().let { sorted ->
            sorted.forEachIndexed { i, n ->
                // Two Pointer Approach
                var p1 = i + 1
                var p2 = nums.size - 1
                while (p1 < p2) {
                    val sum = sorted[p1] + sorted[p2]
                    when {
                        sum == -1 * n -> {
                            results.add(listOf(n, sorted[p1], sorted[p2]))
                            p1++
                        }
                        sum > -1 * n -> p2--
                        sum < -1 * n -> p1++
                    }
                }
            }
        }
        return results.toList()
    }

    @Test
    fun example1() {
        Assertions.assertThat(
            threeSum(intArrayOf(-1, 0, 1, 2, -1, -4))
        ).isEqualTo(listOf(listOf(-1, -1, 2), listOf(-1, 0, 1)))
    }

    @Test
    fun failingTest1() {
        Assertions.assertThat(
            threeSum(intArrayOf(-2, 0, 1, 1, 2))
        ).isEqualTo(listOf(listOf(-2, 0, 2), listOf(-2, 1, 1)))
    }

