package coding.leetcode

import org.assertj.core.api.Assertions.assertThat
import org.junit.jupiter.api.Test
import kotlin.math.max
import kotlin.math.min

class Leet686RepeatedStringSearchTest {

    private fun repeatedStringMatch(a: String, b: String): Int {
        for (i in 0..max(2, 3 * (b.length / a.length))) {
            if (a.repeat(i).contains(b)) {
                return i
            }
        }
        return -1
    }


    @Test
    fun example1() {
        assertThat(repeatedStringMatch("abcd", "cdabcdab")).isEqualTo(3)
    }

    @Test
    fun example2() {
        assertThat(repeatedStringMatch("a", "aa")).isEqualTo(2)
    }

    @Test
    fun example3() {
        assertThat(repeatedStringMatch("aa", "a")).isEqualTo(1)
    }

    @Test
    fun example4() {
        assertThat(repeatedStringMatch("aaaaaaaaaaaaaaaaaaaaaab", "ba")).isEqualTo(2)
    }

    @Test
    fun example5() {
        assertThat(repeatedStringMatch("baa", "abaab")).isEqualTo(3)
    }

    // Times out on leedcode, TBD
    @Test
    fun example6() {
        assertThat(
            repeatedStringMatch(
                "baa", """
            
        """.trimIndent()
            )
        ).isEqualTo(-1)
    }
}